    linkedin: { maxChars: 3000, name: 'LinkedIn' },
    instagram: { maxChars: 2200, name: 'Instagram' },
    pinterest: { maxChars: 500, name: 'Pinterest' },
    tiktok: { maxChars: 2200, name: 'TikTok' },
    youtube: { maxChars: 5000, name: 'YouTube' },
